import matplotlib.pyplot as plt

from typing import List
from shiny import *
from shiny.types import NavSetArg
from shiny.ui import h4
from shiny import App, render, ui

# global
tickers = {"AAPL": "Apple", "MSFT": "Microsoft", "GOOG": "Google", "AMZN": "Amazon"}

from shiny import App, render, ui

app_ui = ui.page_fluid(
    ui.tags.script(
       """
const dataURL = 'https://eodhistoricaldata.com/api/eod/MCD.US?from=2017-01-05&to=2017-02-10&period=d&fmt=json&api_token=demo';
let globalData;
fetchData = async (URL) =>{
  const response = fetch(URL);
  const data = await response;
  return data.json();
}
fetchData(dataURL).then(data => {
    globalData = data;
    console.log(data);
});

let search = window.parent.location.search.substring(1);
let searchArray = search.split('&');
let searchObject = {};
for(item of searchArray){
    let array = item.split(/=(.*)/s);
    let key = array[0];
    let value = array[1];
    searchObject[key] = value;
}

$(document).on('shiny:connected', function(event) {     

    Shiny.setInputValue("aapl", globalData);
    Shiny.setInputValue("search_object", JSON.stringify(searchObject));
    Shiny.setInputValue("email", searchObject['email']);
    Shiny.setInputValue("name", searchObject['name']);
    Shiny.setInputValue("stocks", searchObject['stocks']);
}); 
       """
    ),
    ui.h1("Stock Vision"),
    ui.h2("Query String:"),
    ui.output_text_verbatim("search"),
    ui.h2("Email:"),
    ui.output_text_verbatim("email"),
    ui.h2("Name:"),
    ui.output_text_verbatim("name"),
    ui.h2("Stocks:"),
    ui.output_text_verbatim("stocks"),
    ui.h2("AAPL JS fetch:"),
    ui.output_text_verbatim("aapl"),
        ui.navset_pill(
            ui.nav("predictions", ui.output_ui("predictions_tab")),
            ui.nav("validation", ui.output_ui("validation_tab")),
            ui.nav("playground", ui.output_ui("playground_tab")),
            ui.nav("sandbox", ui.output_ui("sandbox_tab")),
    )
)

def server(input, output, session):
    @output
    @render.text
    def search():
        return f"Query String: {input.search_object()}"

    @output
    @render.text
    def email():
        return f"User email: {input.email()}"

    @output
    @render.text
    def name():
        return f"User name: {input.name()}"

    @output
    @render.text
    def stocks():
        return f"User stocks: {input.stocks().split(',')}"

    @output
    @render.text
    def aapl():
        return f"AAPL stock: {input.appl()}"

    @output
    @render.ui
    def predictions_tab():
        return ui.TagList(
        # main container div
        ui.tags.div(
            ui.input_select(id="ticker", label="Ticker:", choices=input.stocks().split(',')),
            ui.output_plot("example_plot"),
            ui.output_table("example_table"),
            id="container")
        )

    # plot
    @output
    @render.plot
    def example_plot():
        # params
        data = [1, 2, 3, 4, 5]
        fig = plt.plot(data) 

        return fig

app = App(app_ui, server)
